export const ATTRIBUTE_MAP: Record<string, AltairAttribute> = Object.fromEntries(
  ALTAIR_ATTRIBUTES.map(a => [a.name, a])
);

export const ATTRIBUTES_BY_CATEGORY: Record<string, AltairAttribute[]> = {};
for (const a of ALTAIR_ATTRIBUTES) {
  if (!ATTRIBUTES_BY_CATEGORY[a.category]) ATTRIBUTES_BY_CATEGORY[a.category] = [];
  ATTRIBUTES_BY_CATEGORY[a.category].push(a);
}

export function getFriendlyName(attrName: string): string {
  return ATTRIBUTE_MAP[attrName]?.friendlyName || attrName.replace(/_/g, ' ');
}

export function formatAttributeValue(attrName: string, value: any): string {
  if (value === null || value === undefined) return '\u2014';
  const attr = ATTRIBUTE_MAP[attrName];
  if (!attr) return String(value);
  if (attr.filterType === 'flag') return value ? 'Yes' : 'No';
  if (attrName === 'income_est') return `$${Number(value).toLocaleString()}k`;
  if (attrName === 'dti_ratio' || attrName.startsWith('util_') || attrName === 'cltv' || attrName === 'auto_paid_thru') return `${value}%`;
  if (attrName === 'mtg_rate' || attrName === 'auto_est_apr') return `${value}%`;
  if (attrName.includes('_bal') || attrName.includes('_amt') || attrName === 'estimatedvalue' || attrName.includes('high_credit') || attrName.includes('mon_pmt') || attrName.includes('_pmt')) return `$${Number(value).toLocaleString()}`;
  return String(value);
}

